// import './db.js'; 

import { v4 as uuidv4 } from 'uuid';

const idOne = uuidv4();
const idTwo = uuidv4();

// pseudo database
let users = {
    [idOne]: {
        id: idOne,
        firstName: 'Robin',
        lastName: 'Wieruch',
        isDeveloper: true,
      },
      [idTwo]: {
        id: idTwo,
        firstName: 'Dave',
        lastName: 'Davddis',
        isDeveloper: false,
      },
};

const getUsers = (id) =>
  new Promise((resolve, reject) => {

    //   const user = users[id];

    if (!users) {
      return setTimeout(
        () => reject(new Error('Users not found')),
        250
      );
    }

    setTimeout(() => resolve(Object.values(users)), 250);
    // setTimeout(() => resolve(users[id]), 250);
});

// usage (1)
getUsers()
  .then((result) => {
    console.log(result);
  })
  .catch((error) => {
    console.log(error);
  });

// usage (2)
const doGetUsers = async (id) => {
  try {
    const result = await getUsers(id);
    console.log(result);
  } catch (error) {
    console.log(error);
  }
};

doGetUsers();
