import users from './db.json'; 

// import { v4 as uuidv4 } from 'uuid';

// const idOne = uuidv4();
// const idTwo = uuidv4();

// pseudo database
// let users = {
//     1: {
//         id: 1,
//         firstName: 'Robin',
//         lastName: 'Wieruch',
//         isDeveloper: true,
//       },
//       2: {
//         id: 2,
//         firstName: 'Dave',
//         lastName: 'Davddis',
//         isDeveloper: false,
//       },
// };

const getUsers = (id) =>
  new Promise((resolve, reject) => {

    //   const user = users[id];

    if (!users) {
      return setTimeout(
        () => reject(new Error('Users not found')),
        250
      );
    }

    setTimeout(() => resolve(Object.values(users)), 250);
    // setTimeout(() => resolve(users[id]), 250);
});

// usage (1)
getUsers()
  .then((result) => {
    console.log(result);
  })
  .catch((error) => {
    console.log(error);
  });

// usage (2)
const doGetUsers = async (id) => {
  try {
    const result = await getUsers(id);
    console.log(result);
  } catch (error) {
    console.log(error);
  }
};

doGetUsers();

const createUser = (data) =>
  new Promise((resolve, reject) => {
    if (!data.firstName || !data.lastName) {
      reject(new Error('Not all information provided'));
    }

    const id = '3';
    const newUser = { id, ...data };

    users = { ...users, [id]: newUser };

    setTimeout(() => resolve(true), 250);
  });

// usage
const doCreateUser = async (data) => {
  try {
    const result = await createUser(data);
    console.log(result);
  } catch (error) {
    console.log(error);
  }
};

doCreateUser({ firstName: 'Liam', lastName: 'Wieruch' });
