import { FormControl, FormGroup, Input, InputLabel, Button } from '@mui/material';
import { useState } from 'react';
import doCreateUser from '../service/add.js';
import './add.css';


const initialValues = {
    firstname : '',
    lastname: '',
    email: '',
    avatar: ''
}

export const Add = () => {

    let [user, setUser] = useState(initialValues);

    const onValueChange = (e) => {
        setUser({
            ...user,
            [e.target.name]: e.target.value
        });

        console.log(user);
    }

    const addUser = () => {
        // console.log(e.target.firstname);
        doCreateUser(user);
    }

    return (
        <div className="add">
            <div className="container">
                <div className="page__header">
                    <h1>Add User</h1>
                </div>
                <FormGroup>
                    <FormControl>
                        <InputLabel>First Name</InputLabel>
                        <Input onChange={(e) => onValueChange(e)} name="firstname" />
                    </FormControl>
                    <FormControl>
                        <InputLabel>Last Name</InputLabel>
                        <Input onChange={(e) => onValueChange(e)} name="lastname" />
                    </FormControl>
                    <FormControl>
                        <InputLabel>Email</InputLabel>
                        <Input onChange={(e) => onValueChange(e)} name="email" />
                    </FormControl>
                    <FormControl>
                        <InputLabel>Avatar</InputLabel>
                        <Input onChange={(e) => onValueChange(e)} name="avatar" />
                    </FormControl>
                    <FormControl>
                        <Button variant="contained" className="form-btn" onClick={() => addUser()}>Add</Button>
                    </FormControl>
                </FormGroup>
            </div>
        </div>
  )
}

export default Add;
