import { FormControl, FormGroup, Input, InputLabel, Typography, Button } from '@mui/material';
import { useState } from 'react';
import doCreateUser from '../service/add.js';


const initialValues = {
    firstname : '',
    lastname: '',
    email: '',
    avatar: ''
}

export const Add = () => {

    let [user, setUser] = useState(initialValues);

    const onValueChange = (e) => {
        setUser({
            ...user,
            [e.target.name]: e.target.value
        });

        console.log(user);
    }

    const addUser = (e) => {
        // console.log(e.target.firstname);
        doCreateUser({ first_name: user.firstname, last_name: user.lastname });
    }

    return (
        <div className="add">
            <div className="container">
                <div className="add__header">
                    <Typography variant="h4">Add User</Typography>
                    <FormGroup>
                        <FormControl>
                            <InputLabel>First Name</InputLabel>
                            <Input onChange={(e) => onValueChange(e)} name="firstname" />
                        </FormControl>
                        <FormControl>
                            <InputLabel>Last Name</InputLabel>
                            <Input onChange={(e) => onValueChange(e)} name="lastname" />
                        </FormControl>
                        <FormControl>
                            <InputLabel>Email</InputLabel>
                            <Input onChange={(e) => onValueChange(e)} name="email" />
                        </FormControl>
                        <FormControl>
                            <InputLabel>Avatar</InputLabel>
                            <Input onChange={(e) => onValueChange(e)} name="avatar" />
                        </FormControl>
                        <FormControl>
                            <Button variant="contained" onClick={(e) => addUser(e)}>Add</Button>
                        </FormControl>
                    </FormGroup>
                </div>
            </div>
        </div>
  )
}

export default Add;
